import './App.css';
import React from 'react';
import LetterBox from './LetterBox';
import SingleLetterSearchbar from './SingleLetterSearchBar';

const pics = [
  'noose.png', 
  'head.png', 
  'body.png', 
  'oneArm.png', 
  'twoArms.png', 
  'oneLeg.png', 
  'full.png'
];

const words = ["Morehouse", "Spelman", "Basketball", "Table", "Museum", "Excellent", "Fun", "React"];

class HangmanGame extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      wordList: words,
      curWord: '',
      guessedLetters: [],
      wrongLetters: [],
      livesLeft: 7,
      gameOver: false,
      gameWon: false
    };
  }

  componentDidMount() {
    this.startNewGame();
  }

  startNewGame = () => {
    const newWord = this.state.wordList[
      Math.floor(Math.random() * this.state.wordList.length)
    ].toUpperCase();
    this.setState({
      curWord: newWord,
      guessedLetters: [],
      wrongLetters: [],
      livesLeft: 7,
      gameOver: false,
      gameWon: false
    });
  };

  handleLetterGuess = (letter) => {
    letter = letter.toUpperCase();
    const { curWord, guessedLetters, wrongLetters, livesLeft, gameOver } = this.state;
    if (gameOver || guessedLetters.includes(letter) || wrongLetters.includes(letter)) return;

    if (curWord.includes(letter)) {
      const updatedGuessed = [...guessedLetters, letter];
      const isWin = curWord.split('').every((l) => updatedGuessed.includes(l));
      this.setState({
        guessedLetters: updatedGuessed,
        gameWon: isWin,
        gameOver: isWin
      });
    } else {
      const updatedLives = livesLeft - 1;
      this.setState({
        wrongLetters: [...wrongLetters, letter],
        livesLeft: updatedLives,
        gameOver: updatedLives === 0
      });
    }
  };

  renderWordDisplay = () => {
    const { curWord, guessedLetters } = this.state;
    return (
      <div className="word-display">
        {curWord.split('').map((letter, index) => (
          <span key={index} className="letter-blank">
            {guessedLetters.includes(letter) ? letter : '_'}
          </span>
        ))}
      </div>
    );
  };

  renderAlphabet = () => {
    const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    const { guessedLetters, wrongLetters } = this.state;

    const getLetterClass = (letter) => {
      if (guessedLetters.includes(letter)) return 'correct';
      if (wrongLetters.includes(letter)) return 'wrong';
      return '';
    };

    const left = alphabet.slice(0, 13);
    const right = alphabet.slice(13);

    return (
      <div className="alphabet-container">
        <div className="letter-box-group">
          {left.map((l) => (
            <LetterBox key={l} letter={l} boxClass={getLetterClass(l)} />
          ))}
        </div>
        <div className="letter-box-group">
          {right.map((l) => (
            <LetterBox key={l} letter={l} boxClass={getLetterClass(l)} />
          ))}
        </div>
      </div>
    );
  };

  renderHangman = () => {
    const { wrongLetters } = this.state;
    return (
      <div className="hangman-images">
        {pics.slice(0, wrongLetters.length).map((src, i) => (
          <img key={i} src={src} alt={`stage-${i}`} className="hangman-part" />
        ))}
      </div>
    );
  };

  render() {
    const { wrongLetters, gameOver, gameWon } = this.state;

    return (
      <div className="game-container">
        <h1 className="title">Hangman Game</h1>

        {this.renderHangman()}

        {this.renderWordDisplay()}

        <div className="wrong-letters">
          <p>Wrong Letters:</p>
          <span>{wrongLetters.join(', ')}</span>
        </div>

        {this.renderAlphabet()}

        {!gameOver && !gameWon && (
          <SingleLetterSearchbar onSearch={this.handleLetterGuess} />
        )}

        {(gameOver || gameWon) && (
          <button className="play-again-btn" onClick={this.startNewGame}>
            Play Again
          </button>
        )}

        {gameWon && <p className="game-status win">ðŸŽ‰ You Won! ðŸŽ‰</p>}
        {gameOver && !gameWon && <p className="game-status lose">ðŸ’€ You Lost ðŸ’€</p>}
      </div>
    );
  }
}

export default HangmanGame;
